mapper.update:
UPDATE tbl_productoption
	SET
	po_quantity = po_quantity - #{order_quantity}
	WHERE
	productOption_seq = #{order_poseq}

UPDATE tbl_user
	SET
	user_point = #{changedPoint}
	WHERE
	user_seq = #{user_seq}

mapper.insert:
insert into tbl_productoption (productoption_seq, product_seq, po_name, po_quantity, po_price
) 

 select case count(*) when 0 then 1 else max(productoption_seq) + 1 end,#{product_seq}
 ,#{po_name}
 ,#{po_quantity}
 ,#{po_price} from tbl_productoption

mapper.select:
SELECT product_seq, product_name, product_price, product_quantity, product_seller, product_filename, product_info, category_seq, product_readcnt, product_status, product_regdate, product_updatedate, user_nickname, product_like FROM ( SELECT ROW_NUMBER() OVER ( ORDER BY
product_seq DESC
product_like DESC
product_price DESC
product_price
product_readcnt DESC
product_seq DESC
) rn, product_seq, product_name, product_price, product_quantity, product_seller, product_filename, product_info, category_seq, product_readcnt, product_status, product_regdate, product_updatedate, user_nickname, product_like FROM tbl_product
)as a WHERE rn BETWEEN (#{pageNum} - 1) * #{amount} + 1 AND #{pageNum} * #{amount}
