mapper.select:
SELECT user_id, user_password, user_name, user_phone, user_regdate 
FROM tbl_user 
WHERE user_phone = #{user_phone}

mapper.update:
UPDATE tbl_user
SET
   user_grade = #{user_grade}
WHERE
   user_id = #{user_id}

UPDATE tbl_user
SET
   user_password = #{user_password}
WHERE
   user_id = #{user_id}
